$(function(){$(".styleswitch_cont").append('<span class="fader"></span>');$(".styleswitch_cont .fader").fadeTo(1500,1,function(){$(".styleswitch_cont").slideDown(500)});$("a.styleswitch").click(function(){var f=this.getAttribute("id").split("_");var h=f[0];var e=f[1];var c=1;var d=new Array();var g=0;if(e==undefined){$("link[rel*=style][title^="+h+"]").each(function(){g++;var i=this.getAttribute("title").split("_");if(readCookie(h)==i[1]){c=g}d[g]=i[1]});var b=(g>c)?(c+1):1;e=d[b]}switchStylestyle(h,e);return false});var a=new Array();$("link[rel*=style][title]").each(function(d){var e=false;var b=this.getAttribute("title").split("_");$.each(a,function(g,c){if(c==b[0]){e=true}});if(!e){a.push(b[0]);var f=readCookie(b[0]);if(f==undefined){$("link[rel*=default][title^="+b[0]+"]").each(function(){var c=this.getAttribute("title").split("_");switchStylestyle(c[0],c[1])})}else{switchStylestyle(b[0],f)}}})});function switchStylestyle(b,a){$("link[rel*=style][title]").each(function(d){var c=this.getAttribute("title").split("_");if(c[0]==b){this.disabled=true;$("a.styleswitch[id^="+c[0]+"][id$="+c[1]+"]").removeClass("current");if(c[1]==a){this.disabled=false;$("a.styleswitch[id^="+c[0]+"][id$="+c[1]+"]").addClass("current")}}});createCookie(b,a,365)}function createCookie(c,d,e){if(e){var b=new Date();b.setTime(b.getTime()+(e*24*60*60*1000));var a="; expires="+b.toGMTString()}else{var a=""}document.cookie=c+"="+d+a+"; path=/"}function readCookie(b){var e=b+"=";var a=document.cookie.split(";");for(var d=0;d<a.length;d++){var f=a[d];while(f.charAt(0)==" "){f=f.substring(1,f.length)}if(f.indexOf(e)==0){return f.substring(e.length,f.length)}}}function eraseCookie(a){createCookie(a,"",-1)};